def update_jsons():
    for lang in ["uk", "ru", "en"]:
        path = BASE_DIR / "locales" / f"{lang}.json"
        with open(path, 'r', encoding='utf-8') as f:
            data = json.load(f)
        
        for section, keys in FULL_TRANSLATIONS.items():
            if section not in data:
                data[section] = {}
            for key, trans in keys.items():
                data[section][key] = trans[lang]
        
        with open(path, 'w', encoding='utf-8') as f:
            json.dump(data, f, ensure_ascii=False, indent=4)
        print(f"Updated {lang}.json with {sum(len(v) for v in FULL_TRANSLATIONS.values())} translations")

if __name__ == "__main__":
    print("Adding COMPLETE translations for all pages...")
    update_jsons()
    print("Done! Now need to add data-i18n attributes to HTML files.")
